+import React, { useState } from 'react';
+import { View, Text, StyleSheet, TextInput, TouchableOpacity, Switch, ScrollView } from 'react-native';
+import { useColorScheme } from 'react-native';
+import DateTimePicker from '@react-native-community/datetimepicker';
+
+export default function CreateScreen() {
+ const colorScheme = useColorScheme();
+ const isDark = colorScheme === 'dark';
+
+ const [subject, setSubject] = useState('');
+ const [classroom, setClassroom] = useState('');
+ const [date, setDate] = useState(new Date());
+ const [time, setTime] = useState(new Date());
+ const [showDatePicker, setShowDatePicker] = useState(false);
+ const [showTimePicker, setShowTimePicker] = useState(false);
+ const [earlyNotification, setEarlyNotification] = useState(true);
+ const [notes, setNotes] = useState('');
+ const [selectedColor, setSelectedColor] = useState('#7B2CBF');
+
+ const colors = [
+ '#7B2CBF', // Purple (default)
+ '#FF5733', // Red
+ '#33A8FF', // Blue
+ '#33FF57', // Green
+ '#F3FF33', // Yellow
+ '#FF33A8', // Pink
+ ];
+
+ const onDateChange = (event, selectedDate) => {
+ const currentDate = selectedDate || date;
+ setShowDatePicker(false);
+ setDate(currentDate);
+ };
+
+ const onTimeChange = (event, selectedTime) => {
+ const currentTime = selectedTime || time;
+ setShowTimePicker(false);
+ setTime(currentTime);
+ };
+
+ const formatDate = (date) => {
+ return date.toLocaleDateString('en-US', { weekday: 'short', month: 'short', day: 'numeric' });
+ };
+
+ const formatTime = (time) => {
+ return time.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' });
+ };
+
+ return (
+ <ScrollView 
+ style={[
+ styles.container, 
+ { backgroundColor: isDark ? '#111827' : '#f9fafb' }
+ ]}
+ contentContainerStyle={styles.contentContainer}
+ >
+ <Text style={[
+ styles.headerTitle, 
+ { color: isDark ? '#f9fafb' : '#1f2937' }
+ ]}>
+ Create New Alarm
+ </Text>
+
+ <View style={styles.formGroup}>
+ <Text style={[
+ styles.label, 
+ { color: isDark ? '#d1d5db' : '#4b5563' }
+ ]}>
+ Subject
+ </Text>
+ <TextInput
+ style={[
+ styles.input,
+ { 
+ backgroundColor: isDark ? '#1f2937' : '#ffffff',
+ color: isDark ? '#f9fafb' : '#1f2937',
+ borderColor: isDark ? '#374151' : '#e5e7eb',
+ }
+ ]}
+ placeholder="Enter subject name"
+ placeholderTextColor={isDark ? '#6b7280' : '#9ca3af'}
+ value={subject}
+ onChangeText={setSubject}
+ />
+ </View>
+
+ <View style={styles.formGroup}>
+ <Text style={[
+ styles.label, 
+ { color: isDark ? '#d1d5db' : '#4b5563' }
+ ]}>
+ Classroom
+ </Text>
+ <TextInput
+ style={[
+ styles.input,
+ { 
+ backgroundColor: isDark ? '#1f2937' : '#ffffff',
+ color: isDark ? '#f9fafb' : '#1f2937',
+ borderColor: isDark ? '#374151' : '#e5e7eb',
+ }
+ ]}
