+// This is a mock implementation of AsyncStorage for development purposes
+const storage: Record<string, string> = {};
+
+export default {
+ getItem: async (key: string): Promise<string | null> => {
+ return storage[key] || null;
+ },
+ 
+ setItem: async (key: string, value: string): Promise<void> => {
+ storage[key] = value;
+ },
+ 
+ removeItem: async (key: string): Promise<void> => {
+ delete storage[key];
+ },
+ 
+ clear: async (): Promise<void> => {
+ Object.keys(storage).forEach(key => {
+ delete storage[key];
+ });
+ },
+ 
+ getAllKeys: async (): Promise<string[]> => {
+ return Object.keys(storage);
+ },
+ 
+ multiGet: async (keys: string[]): Promise<[string, string | null][]> => {
+ return keys.map(key => [key, storage[key] || null]);
+ },
+ 
+ multiSet: async (keyValuePairs: [string, string][]): Promise<void> => {
+ keyValuePairs.forEach(([key, value]) => {
+ storage[key] = value;
+ });
+ },
+ 
+ multiRemove: async (keys: string[]): Promise<void> => {
+ keys.forEach(key => {
+ delete storage[key];
+ });
+ },
+};","refToolCallId":"toolu_01EV8oobU6UQwg6LZfSwQxYQ"},{"type":"text","text":"Now let's update the StorageService.ts to use our mock implementation:"},{"type":"toolCall","toolCallId":"toolu_01D19UJjiZZLkR8tRKioj9vq","statusText":"Change generated","status":"SUCCESS","context":{"files":[{"path":"ClassAlarm/services/StorageService.ts","fsPath":"D:\FSIPD\project\alarm\ClassAlarm\services\StorageService.ts"}]},"isHidden":false},{"type":"codeHunk","lineBasedDiff":"--- ClassAlarm/services/StorageService.ts
