import { View, Text, StyleSheet, ScrollView, TouchableOpacity, ActivityIndicator } from "react-native";
import { useColorScheme } from "react-native";
import { Ionicons } from "@expo/vector-icons";
import { useAlarms } from "../../context/AlarmContext";
import { router } from "expo-router";
import { Alarm } from "../../types/Alarm";

export default function DashboardScreen() {
 const colorScheme = useColorScheme();
 const isDark = colorScheme === "dark";
 const { alarms, loading, error, refreshAlarms } = useAlarms();
 const [upcomingAlarms, setUpcomingAlarms] = useState<(Alarm & { date: string })[]>([]);

 // Process alarms to get upcoming ones
 useEffect(() => {
 if (alarms.length > 0) {
 const today = new Date();
 const dayMap: { [key: string]: number } = {
 "Sun": 0, "Mon": 1, "Tue": 2, "Wed": 3, "Thu": 4, "Fri": 5, "Sat": 6
 };

 const upcoming = alarms
 .filter(alarm => alarm.isActive && alarm.days && alarm.days.length > 0)
 .map(alarm => {
 // Find the next occurrence of this alarm
 const currentDay = today.getDay();
 const alarmDays = alarm.days!.map(day => dayMap[day]);

 // Sort days by next occurrence
 alarmDays.sort((a, b) => {
 const daysUntilA = (a - currentDay + 7) % 7;
 const daysUntilB = (b - currentDay + 7) % 7;
 return daysUntilA - daysUntilB;
 });

 const nextDay = alarmDays[0];
 const daysUntil = (nextDay - currentDay + 7) % 7;

 // Create a date string
 let dateStr = "";
 if (daysUntil === 0) {
 dateStr = "Today";
 } else if (daysUntil === 1) {
 dateStr = "Tomorrow";
 } else {
 const dayNames = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
 dateStr = dayNames[nextDay];
 }

 return { ...alarm, date: dateStr, daysUntil };
 })
 .sort((a, b) => {
 // Sort by days until
 if (a.daysUntil !== b.daysUntil) {
 return a.daysUntil - b.daysUntil;
 }

 // If same day, sort by time
 const [aTime, aPeriod] = a.time.split(" ");
 const [bTime, bPeriod] = b.time.split(" ");

 const [aHour, aMinute] = aTime.split(":").map(Number);
 const [bHour, bMinute] = bTime.split(":").map(Number);

 // Convert to 24-hour format for comparison
 let aHour24 = aHour;
 if (aPeriod === "PM" && aHour < 12) aHour24 += 12;
 if (aPeriod === "AM" && aHour === 12) aHour24 = 0;

 let bHour24 = bHour;
 if (bPeriod === "PM" && bHour < 12) bHour24 += 12;
 if (bPeriod === "AM" && bHour === 12) bHour24 = 0;

 if (aHour24 !== bHour24) {
 return aHour24 - bHour24;
 }

 return aMinute - bMinute;
 })
 .slice(0, 5); // Get only the next 5 alarms

 setUpcomingAlarms(upcoming);
 } else {
 setUpcomingAlarms([]);
 }
 }, [alarms]);

 const renderUpcomingAlarm = (alarm: Alarm & { date: string }) => (
 <TouchableOpacity 
 key={alarm.id}
 style={[
 styles.alarmCard,
 { backgroundColor: isDark ? "#1f2937" : "#ffffff" }
 ]}
 onPress={() => router.push("/(tabs)/alarms")}
 >
 <View style={[styles.colorIndicator, { backgroundColor: alarm.color }]} />
 <View style={styles.alarmInfo}>
 <Text style={[
 styles.subjectText, 
 { color: isDark ? "#f9fafb" : "#1f2937" }
 ]}>
 {alarm.subject}
 </Text>
 <Text style={[
 styles.classroomText, 
 { color: isDark ? "#9ca3af" : "#4b5563" }
 ]}>
 {alarm.classroom}
 </Text>
 <View style={styles.alarmMeta}>
 <Text style={[
 styles.dateText, 
 { color: isDark ? "#d1d5db" : "#6b7280" }
 ]}>
 {alarm.date}
 </Text>
 <Text style={[
 styles.timeText, 
 { color: isDark ? "#d1d5db" : "#6b7280" }
 ]}>
 {alarm.time}
 </Text>
 </View>
 </View>
 <TouchableOpacity style={styles.bellIcon}>
 <Ionicons name="notifications" size={24} color={isDark ? "#9ca3af" : "#6b7280"} />
 </TouchableOpacity>
 </TouchableOpacity>
 );

 return (
 <View 
 style={[
 styles.container, 
 { backgroundColor: isDark ? "#111827" : "#f9fafb" }
 ]}
 >
 <View style={styles.headerContainer}>
 <Text style={[
 styles.headerTitle, 
 { color: isDark ? "#f9fafb" : "#1f2937" }
 ]}>
 Dashboard
 </Text>

 <TouchableOpacity 
 style={styles.refreshButton} 
 onPress={refreshAlarms}
 disabled={loading}
 >
 <Ionicons 
 name="refresh" 
 size={20} 
 color={isDark ? "#d1d5db" : "#4b5563"} 
 style={loading ? styles.rotating : undefined} 
 />
 </TouchableOpacity>
 </View>

 {error && (
 <View style={styles.errorContainer}>
 <Text style={styles.errorText}>{error}</Text>
 <TouchableOpacity onPress={refreshAlarms}>
 <Text style={styles.retryText}>Retry</Text>
 </TouchableOpacity>
 </View>
 )}

 <ScrollView 
 style={styles.scrollView}
 contentContainerStyle={styles.scrollContent}
 showsVerticalScrollIndicator={false}
 >
 <View style={styles.section}>
 <Text style={[
 styles.sectionTitle, 
 { color: isDark ? "#f9fafb" : "#1f2937" }
 ]}>
 Upcoming Alarms
 </Text>

 {loading ? (
 <View style={styles.loadingContainer}>
 <ActivityIndicator size="large" color="#7B2CBF" />
 <Text style={[
 styles.loadingText,
 { color: isDark ? "#d1d5db" : "#4b5563" }
 ]}>
 Loading alarms...
 </Text>
 </View>
 ) : upcomingAlarms.length > 0 ? (
 <View style={styles.alarmsList}>
 {upcomingAlarms.map(renderUpcomingAlarm)}
 </View>
 ) : (
 <View style={styles.emptyContainer}>
 <Ionicons name="notifications" size={32} color={isDark ? "#4b5563" : "#9ca3af"} />
